import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {faTimesCircle} from '@fortawesome/free-solid-svg-icons';
import Dropdown from 'react-bootstrap/Dropdown';

class Help extends React.Component {
    constructor(props) {
        super(props);

        this.handleHelp = this.handleHelp.bind(this);

        this.state = {
            needHelp:false,
        };
    }

    handleHelp() {
        let temp_help = this.state.needHelp;
        this.setState({
            needHelp: !temp_help,
        });
    }

    renderHelp() {
        const {needHelp} = this.state;
        let helpMenu = []
        if (needHelp) {
            helpMenu.push(
                <div class="border">
                        helpMenu.push(
                <div class="border">
                   <Dropdown>
                    <Dropdown.Toggle variant="success" id="dropdown-basic">
                        how to use the meme generator 3000
                    </Dropdown.Toggle>

                    <Dropdown.Menu>
                        <Dropdown.Item href="#/action-1">1. search for images</Dropdown.Item>
                        <Dropdown.Item href="#/action-2">2. double click to select an image</Dropdown.Item>
                        <Dropdown.Item href="#/action-3">3. enter text</Dropdown.Item>
                    </Dropdown.Menu>
                    </Dropdown>
                </div>
            );
                    <span class="filled">
                        how to use the meme generator 3000
                    </span>
                    <span class="instructions">
                        <button onClick={this.handleHelp} class="noBorder">
                         <FontAwesomeIcon icon={faTimesCircle}/>
                        </button>
                        {<br/>} 
                            1. search for images {<br/>} 
                            2. double click to select an image {<br/>}
                            3. add text to your image {<br/>}
                    </span>
                </div>
            );
        } else {
            helpMenu.push(
                <span class="border">
                    <span class="filled">
                        how to use the meme generator 3000
                    </span>
                    <button onClick={this.handleHelp} class="filled">
                    </button>

                </span>
            );
        }
        return helpMenu;

    }

    render() {
        let helpMenu = this.renderHelp();
        return (
            <div>
                {helpMenu}
            </div>
        );
    }
}

export default Help;
