import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {faTimesCircle} from '@fortawesome/free-solid-svg-icons';

class Help extends React.Component {
    constructor(props) {
        super(props);

        this.handleHelp = this.handleHelp.bind(this);

        this.state = {
            needHelp:false,
        };
    }

    handleHelp() {
        let temp_help = this.state.needHelp;
        this.setState({
            needHelp: !temp_help,
        });
    }

    renderHelp() {
        const {needHelp} = this.state;
        let helpMenu = []
        if (needHelp) {
            helpMenu.push(
                <div>
                    <span class="instructions">
                    HOW TO USE THE MEME GENERATOR 3000" {<br/>} 
                    <button onClick={this.handleHelp} class="button noBorder">
                         <FontAwesomeIcon icon={faTimesCircle}/>
                    </button>
                        <ol>
                            <li> SEARCH FOR IMAGES {<br/>} </li>
                            <li> DOUBLE CLICK TO SELECT AN IMAGE {<br/>}</li>
                            <li> TYPE TEXT INTO THE TEXT BOX {<br/>}</li>
                        </ol>
                    </span>
                </div>
            );
        } else {
            helpMenu.push(
                <button onClick={this.handleHelp} font-size="12" class="button">
                How to Use the Meme Generator 3000
                {/*<FontAwesomeIcon icon={//faQuestion} />*/}
                </button>
            );
        }
        return helpMenu;

    }

    render() {
        let helpMenu = this.renderHelp();
        return (
            <div>
                {helpMenu}
            </div>
        );
    }
}

export default Help;
