import React from 'react';
import PropTypes from 'prop-types';

class Pagination extends React.Component {
    constructor(props) {
        super(props);
        this.handlePaginationMinus  =  this.handlePaginationMinus.bind(this);
        this.handlePaginationPlus  = this.handlePaginationPlus.bind(this);
        this.renderPrevButton = this.renderPrevButton.bind(this);
        this.state = {
            curPage: 1,
        }
    }

    componentDidUpdate(prevProps) {
        if (prevProps.searchChange !== this.props.searchChange) {
            this.setState ({ curPage: 1, });
        } 
    }
    //increment pageNum
    handlePaginationPlus() {
        let tempCurPage = this.state.curPage;
        // so don't have to rely on setState updating quick enough and so don't have to return a explicit promise
        setTimeout(this.props.fetchPics, 0, this.state.curPage + 1); 
        tempCurPage++;
        this.setState({ curPage: tempCurPage,});
    }

    //decrement pageNum
    handlePaginationMinus() {
        let tempCurPage = this.state.curPage;
        // so don't have to rely on setState updating quick enough and so don't have to return a explicit promise
        setTimeout(this.props.fetchPics, 0, this.state.curPage - 1);
        if (tempCurPage > 1) {tempCurPage--; }
        this.setState({ curPage: tempCurPage, });
    }

    renderPrevButton() {
        const {curPage} = this.state;
        let prevButton = [];
        if (curPage > 1) {
            prevButton.push(
                <span>
                    <button 
                        onClick={this.handlePaginationMinus} 
                        class="button bottom">
                        go to previous page
                    </button>
                </span>
            );
        }
        return prevButton;
    }

    renderNextButton() {
        const picsLength = this.props;
        let nextButton = [];
        if (picsLength !== 0) {
            nextButton.push(
                <span>
                    <button 
                        onClick={this.handlePaginationPlus} 
                        class="button bottom"> 
                            go to next page
                    </button>
                </span>
            );
        }
        return nextButton;
    }

    render() {
        let prevButton = this.renderPrevButton();
        let nextButton = this.renderNextButton();
        return (
            <div>
                {prevButton} {nextButton}
            </div>
        );
    }
}

Pagination.propTypes = {
    searchChange: PropTypes.bool.isRequired,
    fetchPics: PropTypes.func.isRequired,
    picsLength: PropTypes.number.isRequired,
}

export default Pagination;
