import React from 'react';
import './index.css';
import PropTypes from 'prop-types';

class Pagination extends React.Component {
    constructor(props) {
        super(props);

        this.handlePaginationMinus  =  this.handlePaginationMinus.bind(this);
        this.handlePaginationPlus  = this.handlePaginationPlus.bind(this);
        this.renderPrevButton = this.renderPrevButton.bind(this);
        this.state = {
            curPage: 1,
            picsLength: 1, //if pics length is 0, display only prev button
        }
    }

    componentDidUpdate(prevProps) {
        if (prevProps.searchChange != this.props.searchChange) {
            this.setState ({
                curPage: 1,
            });
        } 
    }
    //increment pageNum
    handlePaginationPlus() {
        let tempCurPage = this.state.curPage;

        // so don't have to rely on setState updating quick enough and so don't have to return a explicit promist
        setTimeout(this.props.fetchPics, 0, this.state.curPage + 1); 
        tempCurPage++;
        this.setState({
            curPage: tempCurPage,
        });
    }

    //decrement pageNum
    handlePaginationMinus() {
        let tempCurPage = this.state.curPage;

        // so don't have to rely on setState updating quick enough and so don't have to return a explicit promist
        setTimeout(this.props.fetchPics, 0, this.state.curPage - 1);
        if (tempCurPage > 1) {
            tempCurPage--;
        }
        this.setState({
            curPage: tempCurPage,
        });
    }

    renderPrevButton() {
        const {curPage} = this.state;
        let prevButton = [];
        if (curPage > 1) {
            prevButton.push(
                <span>
                    <button onClick={this.handlePaginationMinus} class="button bottom">
                        go to previous page
                    </button>
                </span>
            );
        }
        return prevButton;
    }

    render() {
        let prevButton = this.renderPrevButton();
        return (
            <div>
                {prevButton}
                <button onClick={this.handlePaginationPlus} class="button bottom"> 
                    go to next page
                </button>
            </div>

        );
    }
}

Pagination.propTypes = {
    searchChange: PropTypes.bool.isRequired,
    fetchPics: PropTypes.func.isRequired,
    picsLength: PropTypes.number.isRequired,
}

export default Pagination;
