import React from 'react';

class Query extends React.Component {
    constructor(props) {
        super(props);

        this.handleSubmit = this.handleChange.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.state = {
            search: "",
            curQuery: "",
            all_data: ""
        };
    }
    handleSubmit(e) {
        e.persist();

        fetch("https://www.flickr.com/search", {credentials: 'same-origin'})
            .then((response => {
                if (!response.ok) throw Error(response.statusText);
                return response.json();
            })
            .then(data => {
                console.log(data);
            })
            .catch((error) => {
                console.log(error);
            }))
      /*.then((data) => {
        this.setState({
          comments: data.comments,
          url: data.url,
        });
      })
      .catch((error) => {
          console.log(error);
      }) */
    }

    handleChange(e) {
        e.persist();
        this.setState({
            curQuery: e.target.value,
          });
    }
    render() {
        const {curQuery} = this.state;
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <input type="text" name="q" onChange={this.handleChange} value={curQuery}/>
                    <input type="submit" value="Search Images" />
                </form>
            </div>

        );
    }
}

export default Query;
