import React from 'react';
import PropTypes from 'prop-types';
import Moveable from "react-moveable";
//import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
//import {faSearch} from '@fortawesome/free-solid-svg-icons';

class Image extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            curText: "",
            image: this.props.img,
        };
    }
    componentDidUpdate(prevProps) {
        if(prevProps.img !== this.props.img
           && this.props.img != "") {
            this.setState({
                image: this.props.img,
            });
        }
    }
    handleSubmit(e) {
        e.preventDefault();
        console.log("inSubmit");
    }

    handleChange(e) {
        console.log("inChange"); 
        e.preventDefault();
        this.setState({
            curText: e.target.value,
        });
    }
    render() {
        const {curText,image} = this.state;
        return (
            <div>
                <div id="selected">
                    {image}
                </div>

               <Moveable
                 target={document.getElementById("selected")}
                 container={null}
                 origin={true}
               />

               <form>
                <input type="text" name="q" onChange={this.handleChange} value={curText}/>
                 <button onSubmit={this.handleSubmit}> Submit </button>
                </form>

            </div>
        );
    }
}

Image.propTypes = {
    img: PropTypes.any.isRequired,
};

export default Image;
