import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {faTimesCircle} from '@fortawesome/free-solid-svg-icons';

class Help extends React.Component {
    constructor(props) {
        super(props);

        this.handleHelp = this.handleHelp.bind(this);

        this.state = {
            needHelp:false,
        };
    }

    handleHelp() {
        let temp_help = this.state.needHelp;
        this.setState({
            needHelp: !temp_help,
        });
    }

    renderHelp() {
        const {needHelp} = this.state;
        let helpMenu = []
        if (needHelp) {
            helpMenu.push(
                <div>
                    <div>
                        <button onClick={this.handleHelp}>
                            <FontAwesomeIcon icon={faTimesCircle}/>
                        </button>
                        <span class="centerRules"><b> RULES: </b></span>
                    </div>
                    <span class="caption">
                        <h1> 
                            Author a Meme Today
                        </h1>
                        <h3>
                            Generate them by {<br/>}
                            1) searching for a picture {<br/>}
                            2) selecting a picture {<br/>}
                            3) telling us what words you want to put on your picture {<br/>}
                            4) (optional:) click the download button to download your meme) {<br/>}
                        </h3>
                    </span>
                    Good luck! {<br/>}
                </div>
            );
        } else {
            helpMenu.push(
                <button onClick={this.handleHelp} font-size="12">
                How to Use the Meme Generator 2000
                {/*<FontAwesomeIcon icon={//faQuestion} />*/}
                </button>
            );
        }
        return helpMenu;

    }

    render() {
        let helpMenu = this.renderHelp();
        return (
            <div>
                {helpMenu}
            </div>
        );
    }
}

export default Help;
